--
-- Структура таблицы `films`
--

CREATE TABLE `films` (
  `id` int(11) NOT NULL,
  `film` varchar(45) DEFAULT NULL,
  `about` longtext,
  `img` varchar(45) DEFAULT NULL,
  `view` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `films`
--

INSERT INTO `films` (`id`, `film`, `about`, `img`, `view`) VALUES
(1, 'Мстители', 'Оставшиеся в живых члены команды Мстителей и их союзники должны разработать новый план, который поможет противостоять разрушительным действиям могущественного титана Таноса. После наиболее масштабной и трагической битвы в истории они не могут допустить ошибку.', 'img/avengers.jpg', 31),
(2, 'Падинктон', 'Познакомьтесь, это медведь по имени Паддингтон из дремучего Перу. Он приехал в Лондон, чтобы обрести семью и стать настоящим английским джентльменом. На пути к этой цели его ожидают невероятные приключения, полные юмора и опасностей.', 'img/md.jpg', 33),
(3, 'Побег из Шоушенка', 'Бухгалтер Энди Дюфрейн обвинен в убийстве собственной жены и ее любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решетки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, обладающий живым умом и доброй душой, находит подход как к заключенным, так и к охранникам, добиваясь их особого к себе расположения.', 'img/pobeg_iz_shayshenko.jpg', 1),
(4, 'Зеленая миля', 'Пол Эджкомб — начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока.', 'img/zelena_mila.jpg', 0),
(5, 'Бойцовский клуб', 'Терзаемый хронической бессонницей и отчаянно пытающийся вырваться из мучительно скучной жизни, клерк встречает некоего Тайлера Дардена, харизматического торговца мылом с извращенной философией. Тайлер уверен, что самосовершенствование — удел слабых, а саморазрушение — единственное, ради чего стоит жить.Пройдет немного времени, и вот уже главные герои лупят друг друга почем зря на стоянке перед баром, и очищающий мордобой доставляет им высшее блаженство. Приобщая других мужчин к простым радостям физической жестокости, они основывают тайный Бойцовский Клуб, который имеет огромный успех. Но в концовке фильма всех ждет шокирующее открытие, которое может привести к непредсказуемым событиям…', 'img/fight_club.jpg', 0),
(6, 'Начало', 'Кобб — талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил. И вот у Кобба появляется шанс исправить ошибки. Его последнее дело может вернуть все назад, но для этого ему нужно совершить невозможное — инициацию. Вместо идеальной кражи Кобб и его команда спецов должны будут провернуть обратное. Теперь их задача — не украсть идею, а внедрить ее. Если у них получится, это и станет идеальным преступлением. Но никакое планирование или мастерство не могут подготовить команду к встрече с опасным противником, который, кажется, предугадывает каждый их ход. Врагом, увидеть которого мог бы лишь Кобб.', 'img/nachalo.jpg', 2),
(7, 'Хантер киллер', 'Американская атакующая субмарина «Хантер Киллер» вместе с подразделением Военно-Морских сил США вступает в крайне рискованную боевую авантюру по спасению мира от катастрофы. Когда время на исходе и каждая минута на счету, элитной команде придется совершить невозможное, чтобы предотвратить конфликт мирового масштаба.', 'img/hunter2.jpg', 0),
(8, '300 Спартанцев', '300 спартанцев (англ. 300) — американский художественный фильм режиссёра Зака Снайдера, вышедший в прокат в 2007 году. Экранизация одноимённого комикса Фрэнка Миллера и Линн Варли 1998 года о битве трёхсот спартанцев под командованием царя Леонида с многотысячной персидской армией при Фермопилах.Сюжет основан на комиксе Фрэнка Миллера, в основу которого легла вольная трактовка реальных событий из греко-персидских войн V века до н. э.\r\n\r\nВ 480 г. до н. э. миллионная армия персидского царя Ксеркса начала вторжение в Грецию. Спартанский царь Леонид и его люди встречают персидскую делегацию, которая требует «землю и воду» — то есть полную покорность персидскому царю Ксерксу. В ответ спартанцы сбрасывают персов в колодец.\r\n\r\nНесмотря на предсказание о поражении со стороны продажных персами эфоров, Леонид при помощи своего близкого друга Артемия собирает 300 воинов и выступает в поход, преграждая персам дорогу в Огненных воротах (Фермопильском ущелье). Там Леонид отвергает помощь местного жителя — горбуна Эфиальта, разрешив ему только ухаживать за ранеными. Эфиальт в ярости выбрасывает отцовское вооружение.\r\n\r\nК Огненным вратам подходит отряд персов, перед началом битвы перс просит спартанцев сложить копья и мечи. На что Леонид отвечает: «Персы, попробуйте их взять».\r\nСпартанцы отбивают многочисленные персидские атаки. На переговорах с Ксерксом Леонид отвергает предложение покориться и стать предводителем всей Греции, «понести боевое знамя Ксеркса в самое сердце Европы». Персы вновь идут в атаку, но все их усилия оказываются тщетными.\r\n\r\nКсеркс предлагает явившемуся к нему Эфиальту бесконечные наслаждения, если тот проведёт персов через секретную тропу в горах. Эфиальт соглашается. Персы окружают отряд спартанцев, все они на прицеле лучников. Однако глашатай поздравляет Леонида с победой и предлагает ему склониться перед Ксерксом. Леонид разоружается и встаёт на колени, но это уловка — внезапно царь вскакивает и бросает копьё, которое ранит Ксеркса, заставив последнего усомниться в своей «божественной силе и бессмертии». Персидские лучники расстреливают спартанцев.\r\n\r\nВ эпилоге перед битвой при Платеях единственный выживший из 300 воинов спартанец Дилий, которого Леонид отпустил с целью рассказать о случившемся, произносит речь перед многочисленным спартанским и греческим войском.', 'img/300_poster.jpg', 1),
(9, 'Остров проклятых', 'Два американских судебных пристава отправляются на один из островов в штате Массачусетс, чтобы расследовать исчезновение пациентки клиники для умалишенных преступников. При проведении расследования им придется столкнуться с паутиной лжи, обрушившимся ураганом и смертельным бунтом обитателей клиники.', 'img/ostrov.jpg', 5);

